#----------------1_Nombre de handle dans cette array----------------
Nombre_Handle = Array_Handle_Twitter.count
puts "Mon array contient #{Nombre_Handle} handle, remercie-moi flemmard !"





#----------------2_Quel est le handle le plus court----------------
Handle_le_plus_court = Array_Handle_Twitter.min_by(&:length)
puts "Le handle le plus court est - #{Handle_le_plus_court} -, Si je vais bien c'est juv@min !"





#----------------3_Nombre de handle avec 5 caractères----------------

# suppression de l'arobase
Handle_sans_arobase = Array_Handle_Twitter.map do |suppression_arobase| suppression_arobase.delete("@")
end

# calcul du nombre de handle avec 5 caractères
Handle_5_caracteres = Handle_sans_arobase.count do |tri| tri.length == 5
end
puts "Il y a #{Handle_5_caracteres} handle avec 5 caractères"





#----------------4_Nombre de handle commençant avec une majuscule----------------

# suppression de l'arobase dans le handle
Handle_sans_arobase2 = Array_Handle_Twitter.map do |suppression_arobase| suppression_arobase.delete("@")
end

# Selection des handle avec majuscule (mais les handle avec l'underscore sont toujours pris en compte)
Handle_avec_Majuscule_Underscore = Handle_sans_arobase2.select do |tri2| tri2.match(/^[A-Z]/)
end

# éjection des handle avec un underscore
Handle_avec_Majuscule = Handle_avec_Majuscule_Underscore.reject do |suppression_underscore| suppression_underscore.start_with?("_")
end

# Calcul du nombre de handle avec une majuscule
Nombre = Handle_avec_Majuscule.count
puts "Il y a #{Nombre} handle commençant par une majuscule"





#----------------5_Liste de handle triée par ordre alphabetiques----------------
Handle_tri = Array_Handle_Twitter.sort
puts "Voici le handle trié par ordre alphabétiques : #{Handle_tri} "





#----------------6_Liste de handle triée par ordre croissant----------------
Handle_ordre_croissant = Array_Handle_Twitter.sort_by(&:length)
puts "Voici le handle trié par ordre croissant : #{Handle_ordre_croissant} "





#----------------7_Position de @epenser----------------
Handle_epenser = Array_Handle_Twitter.index("@epenser")
puts "@epenser est affiché en #{Handle_epenser + 1}eme position dans le handle"





#----------------8_Répartition des handle par taille---------------- 

# Création d'un Hash pour y mettre les répartitions
Mon_Hash = Hash.new(0)

# Classer par ordre croissant les handle dans le Array 
# Puis assigner un nombre de caractères à chaque handle et les mettre dans le Hash
Array_Handle_Twitter.sort_by(&:length).each do |assignation| Mon_Hash[assignation.length] += 1 
end

# Afficher la répartition
Mon_Hash.each do |nombre_de_caracteres, count|
puts "Handle avec #{nombre_de_caracteres} caractères = #{count}"
end



